package estruturaFor;

public class ContagemRegressiva {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Contagem regressiva inicia a váriavel com o valor que se quer e ele vai diminuindo.
		
		// Dividido em 3 partes o for o inicio so executa uma vez. depois vem a condição 
		// nesse caso i=4; e o incremento que e i--.
		for (int i=4; i>=0; i--) {
			System.out.println("Valor de i: " + i);
		}

	}

}
